import { Layout, Typography,Menu } from "antd";
import { useState } from "react";

import LoginForm from "./components/LoginForm";
import SignupForm from "./components/SignupForm";
import AdminHome from "./components/AdminHome";
import ResidentHome from "./components/ResidentHome";
import TopBar from "./components/TopBar";
import Sider from "antd/lib/layout/Sider";

const { Content } = Layout;
const { Title } = Typography;

function App() {
  // const [authed, setAuthed] = useState(false);
  const [authed, setAuthed] = useState(true); //uncomment previous line. temp solution for testing.
  const [asAdmin, setAdmin] = useState(true); //uncomment previous line. temp solution for testing.
  const [isLoggedIn, setLogin] = useState(false);
  console.log("islogged in", isLoggedIn)

  const handelTempLogin =(loginStatus) => {

    setLogin(loginStatus);
    // console.log("after handle", loginStatus)
  };
  console.log("after handle", isLoggedIn)

  const [Reservation, setReservation] = useState(false);


  const handleAnnouncement = () => {
    setReservation(false)
    alert("Announcement is under development")
  }
  const handleDiscussionBoard = () => {
    setReservation(false)
    alert("Dicussion Board is under development")
  }

  const showReservation = () => {
    setReservation(true)
  }

  const renderContent = () => {


    if (isLoggedIn & authed & asAdmin) {
      return <AdminHome />;
    }
    if (isLoggedIn & authed & !asAdmin){
      return <ResidentHome/>
    } //might not be necessary to have this component
    return <LoginForm handelTempLogin = {handelTempLogin}/>;
  };

  return (
    <Layout style={{ height: "100vh" }}>
      <TopBar isLoggedIn={isLoggedIn} asAdmin={asAdmin}/>
      <Layout>
      <Sider className="site-layout-background"
        style={
          { width: "200",
            minHeight: "100%",
          }
        }

        >
          <Menu
            mode="inline"
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            style={{
              height: "100vh",
              borderRight: 0,
            }}
            // items={items}
            >
            <Menu.Item label="Announcement" key={1} onClick={handleAnnouncement}>Announcement</Menu.Item>
            <Menu.Item label="Discussion Board" key={2} onClick={handleDiscussionBoard}>Discussion Board</Menu.Item>
            {<ChatForm />}
            <Menu.Item label="Reservation" key={4} onClick={showReservation}>Reservation</Menu.Item>
          </Menu>

        </Sider>
      <Content>{renderContent()}</Content>
      </Layout>
      {/* <LoginForm handelTempLogin = {handelTempLogin} /> */}
    </Layout>
  );
}

export default App;
