import { Button, Form, Input, message, Checkbox } from "antd";
import React from "react";
import { LockOutlined, UserOutlined } from "@ant-design/icons";
import { login } from "../utils";

class LoginForm extends React.Component {
  state = {
    loading: false,
    isLogin:false,
  };

  handleTempLogin = ()=>{
    this.setState({
      isLogin:true,
    });
  }

  // onFinish = (data) => {
